(function($){$.fn.enable=function(){this.each(function(){$(this).removeAttr("disabled");});return this;};$.fn.disable=function(){this.each(function(){$(this).attr("disabled","disabled");});return this;};$.fn.enabled=function(){if(arguments.length){var v=arguments[0];this.each(function(){if(v){$(this).enable();}else{$(this).disable();}});return this;}else{var r=[];this.each(function(){r.push(!$(this).attr("disabled"));});return r.length>1?r:r[0];}};})(jQuery);
