(function($){function createTagWidget(options){options=$.extend({},options);var div=$("<div/>").addClass("tagwidget").addClass("cloud");var toggleOption=function(event,nochange){var opt=$(this).data("option");if(!nochange){opt.selected=!opt.selected;}$(this).toggleClass("unselected",!opt.selected).toggleClass("selected",opt.selected);};this.find("option").each(function(){var span=$("<span/>").addClass($(this).attr("selected")?"selected":"unselected").text($(this).text()).click(toggleOption).dblclick(toggleOption).data("option",this).appendTo(div);$(this).data("tagwidget-span",span);});this.after(div).css("display","none").data("tagwidget",div).data("tagwidget.options",options).bind("DOMNodeInserted",function(event){var target=event.originalTarget?event.originalTarget:event.target;var span=$("<span/>").addClass($(target).attr("selected")?"selected":"unselected").text($(target).text()).click(toggleOption).dblclick(toggleOption).data("option",target).appendTo(div);$(target).data("tagwidget-span",span);}).bind("DOMNodeRemoved",function(event){var target=event.originalTarget?event.originalTarget:event.target;$(target).data("tagwidget-span").remove();}).bind("tagwidget.update",function(event){var target=event.target?$(event.target):$(this).find("option");target.each(function(){var span=$(this).data("tagwidget-span");if(span&&span.hasClass("selected")!=this.selected){span.trigger("click",[true]);}});});return this;}function modifyTagWidget(option,value){var options=this.data("tagwidget.options");if(typeof value=="undefined"){return options[option];}options[option]=value;return this;}$.fn.tagWidget=function(options,value){if(typeof options=="string"){return modifyTagWidget.apply(this.eq(0),[options,value]);}else{return $(this).each(function(){createTagWidget.apply($(this),[options]);});}};})(jQuery);
